<template>  <div class="md-layout-column">    <md-toolbar class="md-primary">      <md-button class="md-icon-button" @click="show()">        <md-icon>menu</md-icon>      </md-button>      <router-link to="/">        <md-icon class="md-title">move_to_inbox</md-icon>      </router-link>    </md-toolbar>    <md-drawer :md-active.sync="showNavigation" md-swipeable>      <md-toolbar class="md-transparent" md-elevation="0">        <span class="md-title">Основное меню</span>      </md-toolbar>      <md-list>        <md-list-item>          <md-icon>move_to_inbox</md-icon>          <span @click="hide()">            <router-link to="/" class="md-title">Главная</router-link>          </span>        </md-list-item>        <md-list-item>          <md-icon>send</md-icon>          <span @click="hide()">            <router-link to="/tasks" class="md-title">Список заявок</router-link>          </span>        </md-list-item>      </md-list>    </md-drawer>    <ul class="breadcrumbs__list">      <li class="breadcrumbs__item">        <router-link to="/" class="breadcrumbs__link">Главная</router-link>      </li>      <li class="breadcrumbs__item breadcrumbs__item--divider">/</li>      <li class="breadcrumbs__item">        <router-link to="/tasks" class="breadcrumbs__link">Список заявок</router-link>      </li>      <li class="breadcrumbs__item breadcrumbs__item--divider">/</li>      <li class="breadcrumbs__item">        <span class="breadcrumbs__link breadcrumbs__link--active">Заявка {{ $route.params.id }}</span>      </li>    </ul>  </div></template><script>export default {  name: 'Header',  data: () => ({    showNavigation: false,  }),  methods: {    show() {      this.showNavigation = true;    },    hide() {      this.showNavigation = false;    }  }}</script><style lang="scss" scoped>.breadcrumbs {  &__list {    display: flex;    align-items: center;    list-style-type: none;    margin: 0;    padding: 18px 12px;  }  &__item--divider {    padding: 0 12px;  }  &__link {    text-decoration: underline;    &--active {      color: #ddd;      text-decoration: none;    }    &:hover {      text-decoration: none;    }  }}</style>