<template>  <div>    <h1>{{ getTask.name }}</h1>    <h2>{{ getTask.description }}</h2>    <h3>{{ getTask.status }}</h3>    {{ getTask }}  </div></template><script>import task from '../static/tasks.json';export default {  name: 'Task',  data() {    return {      task,    }  },  computed: {    getTask() {      return task.find(({ id }) => id === this.$route.params.id);    }  }}</script>