<template>  <section>    <md-toolbar>      <h1 class="md-title" style="flex: 1; text-align: left">{{ getTask.name }}</h1>      <i :class="`task__text--${getTask.status}`">{{ status[getTask.status] }}</i>      <md-button class="task__btn">Редактировать</md-button>    </md-toolbar>    <md-content class="container task__container">      <strong>{{ getTask.description }}</strong>    </md-content>    <modal :task="getTask" @change-title="changeTitle" @change-description="changeDescription"/>  </section></template><script>import task from '../static/tasks.json';import Modal from '@/components/Modal';export default {  name: 'Task',  components: {Modal},  data() {    return {      task,      status: {        done: 'Выполнено',        active: 'В работе',        overdue: 'Просрочено',      }    }  },  computed: {    getTask() {      return task.find(({ id }) => id === this.$route.params.id);    },  },  methods: {    changeTitle(title) {      this.getTask.name = title;    },    changeDescription(description) {      this.getTask.description = description;    }  }}</script><style lang="scss" scoped> .task {   &__container {    padding-top: 16px;    text-align: left;    background: none !important;   }   &__btn {     background-color: #37ce7e;     border-radius: 4px;     &::v-deep .md-button-content {       font-size: 16px;       font-weight: bold;       color: #fff;       text-transform: none;     }   }   &__text {     &--done {       color: #37ce7e;     }     &--active {       color: #4c7397;     }     &--overdue {       color: #db545a;     }   } }</style>