<template>  <div>    <md-list class="task__list">      <template v-for="task in tasks">        <router-link :to="`/task/${task.id}`" :key="task.id">          <md-list-item              class="task__item md-error"              :class="`task__item--${task.status}`"          >            <span class="task__number">{{ task.id }}</span>            <span>{{ task.name }}</span>            <p>{{ task.description }}</p>          </md-list-item>        </router-link>      </template>    </md-list>  </div></template><script>import tasks from '../static/tasks.json';export default {  name: 'TripleLine',  data() {    return {      tasks,    }  },}</script><style lang="scss" scoped>.task {  &__list {    width: 100%;    max-width: 854px;    display: inline-block;    vertical-align: top;  }  &__item {    height: 200px;    width: 100%;    display: flex;    align-items: center;    &--overdue {      background-color: #db545a;    }    &--done {      background-color: #37ce7e;    }    &--active {      background-color: #4c7397;    }  }  &__number {    height: 100%;    display: flex;    align-items: center;    justify-content: center;  }  &__number::before, {    content: '';    display: block;    position: absolute;    background-color: rgba(255, 255, 255, 0.6);    width: 203px;    min-height: 209px;    top: 0;    bottom: 0;    left: 0;    right: 0;  }}.md-list-item-content {  padding: 0 !important;}.md-list-item-container {  height: 100%;}</style>